class phpTraitTest extends PHPUnit_Framework_TestCase {

  /**
   * @covers phpTrait::usesTrait
   */
  public function testUsesTrait() {
    
    $pClassA  = new phpTraitTest_ClassA();
    $pClassAB = new phpTraitTest_ClassAB();
    $pClassB  = new phpTraitTest_ClassB();
    $pClassC  = new phpTraitTest_ClassC();
    
    $this->assertTrue(
            phpTrait::usesTrait($pClassA, 'phpTraitTest_TraitA'),
            'TraitA not detected in ClassA');
    
    $this->assertTrue(
            phpTrait::usesTrait($pClassAB, 'phpTraitTest_TraitA'),
            'TraitA not detected in ClassAB');
    
    $this->assertTrue(
            phpTrait::usesTrait($pClassAB, 'phpTraitTest_TraitB'),
            'TraitB not detected in ClassAB');
    
    $this->assertTrue(
            phpTrait::usesTrait($pClassB, 'phpTraitTest_TraitB'),
            'TraitB not detected in ClassB');
    
    $this->assertFalse(
            phpTrait::usesTrait($pClassC, 'phpTraitTest_TraitA'),
            'TraitA detected in ClassC');
    
    $this->assertFalse(
            phpTrait::usesTrait($pClassC, 'phpTraitTest_TraitB'),
            'TraitB detected in ClassC');

  }

}

